import React  from 'react';
import {Link} from 'react-router-dom';
import {Header,Image, Grid} from 'semantic-ui-react';
import photo from '../images/dance.jpeg';
import GroupsAvailable from '../Groups/GroupsAvailable';
import firebase from '../Firebase';

class CategoryDetailPage extends React.Component {


    constructor(props) {
        super(props);
        var path = this.props.location.pathname;
        var str = path.substring(2);
        this.state = {name : str};
        console.log(str);
    }

    dataBase = async() => {
        console.log("aaaa");
          var  query = firebase.database().ref("categories").orderByKey();
          await query.once("value")
            .then(function(snapshot) {
              snapshot.forEach(function(childSnapshot) {
                var key = childSnapshot.key;
                var childData = childSnapshot.val().name;
                var description = childSnapshot.val().description;
                console.log(key);
                console.log(childData);
                var x = {"name" : childData, "description": description};
                console.log(x);
            });
          });
        }

    render() {
        console.log(this.props);
        console.log(this.state.name);
        return (
            <div>
                <Grid>
                    <Grid.Row></Grid.Row>
                    <Grid.Row><Header as = "h1" floated = "center">{this.state.name}</Header></Grid.Row>
                    <Grid.Row>
                        <Grid.Column width={13}>
                            <Header as = "h4" floated = "left">Description</Header>
                        </Grid.Column>
                        <Grid.Column width={3}>
                            <Image src = {photo} alt = "image" width = "200" height = "200" floated = "right"/>
                        </Grid.Column>
                        </Grid.Row>
                        <Grid.Row>
                            <GroupsAvailable />
                    </Grid.Row>
                </Grid>




            </div>
        );
    }
}

export default CategoryDetailPage;
