import React from 'react';
import {Header} from 'semantic-ui-react';
import firebase from '../Firebase';
class GroupsAvailable extends React.Component {

    constructor() {
        super();
        this.state = {group : []};
    }

    dataBase = async() => {

          var  query = firebase.database().ref("groups").orderByKey();
          await query.once("value")
            .then(function(snapshot) {
              snapshot.forEach(function(childSnapshot) {
                var key = childSnapshot.key;
                var childData = childSnapshot.val();
                console.log(key);     
                console.log(childData);
                var groupDetails = {name: childData.name, category: childData.category, description: childData.Description};
                console.log(groupDetails);

            });
          });

        }


    componentWillMount() {
        this.dataBase();
    }

    render() {
        return (
            <div>
                <Header as = "h1">Groups Available: </Header>
            </div>
        );
    }
}

export default GroupsAvailable;
