import React  from 'react';
import {Link} from 'react-router-dom';
import {Header,Image, Grid, Button} from 'semantic-ui-react';
import photo from '../images/dance.jpeg';
import GroupsAvailable from '../Groups/GroupsAvailable';
import firebase from '../Firebase';

class CategoryDetailPage extends React.Component {
    constructor(props) {
    super(props);
    var path = this.props.location.pathname;
    var str = path.substring(2);
    this.state = {name : str, categorySelected : {}};
    console.log(str);
    }

    dataBase = async() => {
        console.log("aaaa");
        var categorySelected = this.state.name;
        var x = {};
          var  query = firebase.database().ref().child("categories");
          await query.once("value")
            .then(function(snapshot) {
                console.log("**&*&*&*&*");
                console.log(snapshot);
              snapshot.forEach(function(childSnapshot) {
                var key = childSnapshot.key;
                var category = childSnapshot.val().name;
                if (category === categorySelected) {
                    var description = childSnapshot.val().description;
                    console.log(key);
                    x = {"name" : category, "description": description};
                }
            });
          });
          console.log(x);
          this.setState({categorySelected : x});
        }

    render() {
       //this.dataBase();
        return (
            <div>
                <Grid>
                    <Grid.Row></Grid.Row>
                    <Grid.Row><Header as = "h1" floated = "center">{this.state.name}</Header></Grid.Row>
                    <Grid.Row>
                        <Grid.Column width={13}>
                            <Header as = "h4" floated = "left">Find out what's happening in the {this.state.categorySelected.name} groups.</Header>
                        </Grid.Column>
                        <Grid.Column width={3}>
                            <Image src = {photo} alt = "image" width = "200" height = "200" floated = "right"/>
                        </Grid.Column>
                        </Grid.Row>
                        <Grid.Row>
                        <Button onClick = {this.dataBase.bind(this)}>Refresh</Button>
                            <GroupsAvailable />
                    </Grid.Row>
                </Grid>




            </div>
        );
    }
}

export default CategoryDetailPage;
