import React from 'react';
import {Header} from 'semantic-ui-react';
class GroupsDetailDisplay extends React.Component {

    constructor(props) {
        super(props);
        var path = this.props.location.pathname;
        var str = path.substring(12);
        this.state = {name: str, categorySelected: {}};
        console.log(str);
        }
        dataBase = async() => {
            console.log("aaaa");
            var categorySelected = this.state.name;
            var x = {};
              var  query = firebase.database().ref().child("categories");
              await query.once("value")
                .then(function(snapshot) {
                    console.log("**&*&*&*&*");
                    console.log(snapshot);
                  snapshot.forEach(function(childSnapshot) {
                    var key = childSnapshot.key;
                    var category = childSnapshot.val().name;
                    if (category === categorySelected) {
                        var description = childSnapshot.val().description;
                        //console.log(childSnapshot.val().photo);
                        x = {"name" : category, "description": description, "photo" : childSnapshot.val().photo};
                    }
                });
              });
              console.log(x);
              this.setState({categorySelected : x});
            }

            componentWillMount() {
                this.dataBase();
            }
    render() {
        return (
            <div>
            <Header as = "h1">
            {this.state.name}
            </Header>
            </div>
        );
    }
}

export default GroupsDetailDisplay;
