    {item : "Singing", photo : sing}, 
    {item : "Photography", photo : photo}, 
    {item : "Dance", photo : dance}, 
    {item : "Singing", photo : sing}]
  };
    dataBase = async() => {
        var cats = [];
        console.log("Hello");
        var  query = firebase.database().ref("categories").orderByKey();
        await query.once("value")
          .then(function(snapshot) {
            snapshot.forEach(function(childSnapshot) {
              var key = childSnapshot.key;
              var childData = childSnapshot.val().name;
              var photo = childSnapshot.val().photo;
              console.log(key);
              console.log(childData);
              var x = {"name" : childData, "photo": photo};
              cats.push(x);
          });
        });
        console.log(this.state);
        this.setState({
          name : "Sushma",
          age : "39",
          cat : cats
        });
        console.log("*****");
    console.log(cats);
    console.log("*****");
        // var  query1 = firebase.database().ref("categories");
        // query1.push({address : "asd", age : "20"});

      }

    render() {
        const images = this.state.images.map( (image) => {
            return <HobbyCard name = {image.item} key = {image.item} photo = {image.photo}/>;
        });
        const data = this.state.cat.map( (d) => {
          console.log("1234");
          console.log(d.photo);
          return <HobbyCard name = {d.name} key = {d.name} photo = {d.photo}/>;
        });

        return(
            <div>

                <Header as="h2" floated = "left">
                    Categories
                </Header><br /><br /><br /><br />
                <div className = "image-list">{images}</div><br /><br /><br /><br />

                <Grid>
    <Grid.Column textAlign="center">
    <Button position = "center" primary as = {Link} to = "/createCategory"><Icon name = 'add' primary />Create new Category</Button>
    <Button onClick = {this.dataBase.bind(this)}>Refresh</Button>
    {data}
    </Grid.Column>
  </Grid>
            </div>
        );
    }
}
export default CategoriesDisplay;
