import React  from 'react';
import {Button} from 'semantic-ui-react';
class HobbyCard extends React.Component {
	constructor(props) {
		super(props);
		this.state = {spans : 0, name : this.props.name};
		this.imageRef = React.createRef();
	}
	setSpans = () => {
		const h = this.imageRef.current.clientHeight;
		const span = Math.ceil(h/10);
		//console.log(span);
		this.setState({spans : span});
	}

	clickedEvent(){
		console.log(this.state.name);
	}

	render() {
		return (
			//<Button>
			<div style = {{gridRowEnd : `span ${this.state.spans}` }} onClick={this.clickedEvent.bind} >
				<img
				src = {this.props.photo}
				title = {this.props.name} 
				 />
				 <p>{this.props.name}</p>
			</div>
			// </Button>
		);

	}
}

export default HobbyCard;
