import React from 'react';
import HobbyCard from './HobbyCard';
import './CategoriesDisplay.css';
import {Header, Button, Grid, Icon} from 'semantic-ui-react';
import photo from '../images/photo.jpeg';
import dance from '../images/dance.jpeg';
import sing from '../images/sing.jpeg';


class CategoriesDisplay extends React.Component {
    selectedCategory = () => {
        console.log("Hello");
    }

    state = {images : [{item : "Photography", photo : photo }, 
    {item : "Dance", photo : dance}, 
    {item : "Singing", photo : sing}, 
    {item : "Photography", photo : photo}, 
    {item : "Dance", photo : dance}, 
    {item : "Singing", photo : sing}, 
    {item : "Photography", photo : photo}, 
    {item : "Dance", photo : dance}, 
    {item : "Singing", photo : sing}]};

    render() {
        const images = this.state.images.map( (image) => {
            return <HobbyCard name = {image.item} key = {image.item} photo = {image.photo}/>;
		});
        return(
            <div>
                <Header as="h2" floated = "left">
                    Categories
                </Header><br /><br /><br /><br />
                <div className = "image-list">{images}</div><br /><br /><br /><br />

                <Grid>
    <Grid.Column textAlign="center">
    <Button position = "center" primary><Icon plus/>Create new Category</Button>
    </Grid.Column>
  </Grid>
            </div>
        );
    }
}
export default CategoriesDisplay;
