import React from 'react';
import {Header, Grid, Image} from 'semantic-ui-react';
import firebase from '../Firebase';
class GroupsDetailDisplay extends React.Component {

    constructor(props) {
        super(props);
        var path = this.props.location.pathname;
        var str = path.substring(12);
        this.state = {name: str, categorySelected: {}};
        console.log(str);
        }
        dataBase = async() => {
            console.log("aaaa");
            var categorySelected = this.state.name;
            var x = {};
              var  query = firebase.database().ref().child("groups");
              await query.once("value")
                .then(function(snapshot) {
                    console.log("**&*&*&*&*");
                    console.log(snapshot);
                  snapshot.forEach(function(childSnapshot) {
                    var key = childSnapshot.key;
                    var category = childSnapshot.val().name;
                    if (category === categorySelected) {
                        var description = childSnapshot.val().description;
                        x = {"name" : category, "description": description, "photo" : childSnapshot.val().photo};
                    }
                });
              });
              console.log(x);
              this.setState({categorySelected : x});
            }

            componentWillMount() {
                this.dataBase();
            }
    render() {
        return (
            <div>
            <Grid>
                    <Grid.Row></Grid.Row>
                    <Grid.Row><Header as = "h1" floated = "center">{this.state.name}</Header></Grid.Row>
                    <Grid.Row>
                        <Grid.Column width={13}>
                            <Header as = "h4" floated = "left">{this.state.categorySelected.description}</Header>
                        </Grid.Column>
                        <Grid.Column width={3}>
                            <Image src = {this.state.categorySelected.photo} alt = "image" width = "200" height = "200" floated = "right"/>
                        </Grid.Column>
                        </Grid.Row>
                        <Grid.Row>
                        {/* <Button onClick = {this.dataBase.bind(this)}>Refresh</Button> */}
                    </Grid.Row>
                </Grid>

            </div>
        );
    }
}

export default GroupsDetailDisplay;
